#
# View "v_nota_teraktif"
#

DROP VIEW IF EXISTS `v_nota_teraktif`;
CREATE
  ALGORITHM = UNDEFINED
  VIEW `v_nota_teraktif`
  AS
  SELECT DISTINCT
    `id_nota`, `id_cust`, `nama_cust`, `total_harga`
  FROM
    `trans_jual`
  WHERE
    ((`tanggal_nota` >= 20150316) AND (`tanggal_nota` <= 20150316));
